import { useState } from "react";
import { Link } from 'react-router-dom';

function Home() {
  // Example: Conditional rendering based on authentication state
  const isAuthenticated = useState(); // Set the actual authentication state here

  return (
    <div>
      <h1>Welcome to Less Carbon</h1>

      {/* Conditional rendering based on authentication state */}
      {isAuthenticated ? (
        <div>
          <h2>You have been authenticated</h2>
          {/* Display content for authenticated users */}
        </div>
      ) : (
        <div>
          <h2>Unauthenticated Content</h2>
          {/* Display content for unauthenticated users */}
          <Link to="/login">Login</Link>
          <Link to="/register">Register</Link>
        </div>
      )}

      {/* Add navigation links */}
      {/* <Link to="/dashboard">Dashboard</Link> */}
      <Link to="/posts">Posts</Link>
    </div>
  );
}

export default Home;
