import axios from 'axios';
import api from "./apiConfig";

// const baseUrl = `${api}/api/users`; // Replace with your backend authentication API endpoint

const authService = {
  login: async (email, password) => {
    console.log('logging in')
    const response = await axios.post(`${api}/users/login`, { email, password });
    return response.data.token;
  },

  register: async (name, email, password) => {
    const response = await axios.post(`${api}/users/register`, { name, email, password });
    return response.data.token;
  },

  logout: () => {
    // Clear the token from local storage or cookies
    // For example, if using localStorage:
    localStorage.removeItem('token');
  },

  getUserData: async () => {
    const response = await axios.get(`${baseUrl}/user`);
    return response.data;
  },

  isAuthenticated: () => {
    // Check if the user is authenticated (e.g., check the presence of a valid token)
    // For example, if using localStorage:
    const token = localStorage.getItem('token');
    return !!token; // Return true if token exists, false otherwise
  },
};

export default authService;
