import React from "react";
import { Link } from "react-router-dom";

function ListAllArticles({ article }) {
  return (
    <div>
      <Link to={`/article/${article._id}`}>
        <div className="p-6 bg-white rounded-lg border border-gray-200 shadow-md dark:bg-gray-800 dark:border-gray-700">
          <h3 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{article.title}</h3>
          <div className="flex justify-between items-center">
          <div className="flex items-center space-x-4">
            <img
              className="w-7 h-7 rounded-full"
              src=""
              alt="userPic"
            />
            <span className="font-medium dark:text-white">{article.author}</span>
          </div>
          </div>
        </div>
      </Link>


    </div>
  );
}

export default ListAllArticles;
