import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { getArticle } from '../../services/atricles.js';
import authService from "../../";

function ArticeInfo() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const fetchArticle = async () => {
      try {
        const articleData = await getArticle(id);
        setArticle(articleData);
      } catch (error) {
        console.error('Error fetching article:', error);
      }
    };

    fetchArticle();
  }, [id]);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userData = await authService.getUserData();
        setCurrentUser(userData);
      } catch (error) {
        console.log(error);
      }
    };

    fetchUser();
  }, []);

  if (!article || !currentUser) {
    return <div>Loading...</div>;
  }

  const isCurrentUserAuthor = currentUser.id === article.authorId; // Adjust the condition based on your user data structure

  const handleEditClick = () => {
    if (isCurrentUserAuthor) {
      navigate(`/article/${id}/edit`);
    } else {
      // Handle unauthorized edit attempt
    }
  };

  return (
    <div>
      <h1>Article Info</h1>
      <div>{article.title}</div>
      <p>{article.content}</p>
      <p>{article.author}</p>
      <img src={article.images} alt="Article" />

      {isCurrentUserAuthor && (
        <button onClick={handleEditClick}>Edit Article</button>
      )}
    </div>
  );
}

export default ArticeInfo;
