import axios from 'axios';
import api from "./apiConfig";

const baseUrl = 'http://localhost:8000/api/users'; // Replace with your backend authentication API endpoint

const authService = {
  login: async (email, password) => {
    console.log('logging in')
    const response = await axios.post(`${baseUrl}/login`, { email, password });
    const token = response.data.token;

    // Store the token in localStorage
    localStorage.setItem('token', token);

    return token;
  },

  register: async (name, email, password) => {
    const response = await axios.post(`${baseUrl}`, { name, email, password });
    return response.data.token;
  },

  logout: () => {
    localStorage.removeItem('token');
  },

  getUserData: async () => {
    const token = localStorage.getItem('token');

    const headers = {
      Authorization: `Bearer ${token}`,
    };



    try {
      const response = await axios.get(`${baseUrl}/me`, { headers });
      return response.data;
    } catch (error) {
      // Handle error
      throw error;
    }
  },

  isAuthenticated: () => {
    // Check if the user is authenticated (e.g., check the presence of a valid token)
    // For example, if using localStorage:
    const token = localStorage.getItem('token');
    console.log(!!token)
    return !!token; // Return true if token exists, false otherwise
  },
};

export default authService;
