import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { updateArticle, getArticle } from "../../services/atricles.js";

function ArticleEdit() {
  const [article, setArticle] = useState({
    title: "",
    content: "",
    images: [],
  });

  let navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const fetchArticle = async () => {
      let articleToEdit = await getArticle(id);
      setArticle(articleToEdit);
    };

    fetchArticle();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setArticle((prevArticle) => ({
      ...prevArticle,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await updateArticle(id, article);
    navigate(`/article/${id}`, { replace: true });
  };

  return (
    <div>
      <h2>Edit Article</h2>

      <form onSubmit={handleSubmit}>
        <input
          placeholder="Title"
          name="title"
          value={article.title}
          onChange={handleChange}
        />
        <textarea
          placeholder="Content"
          name="content"
          value={article.content}
          onChange={handleChange}
          rows={5}
        />
        <input
          placeholder="Image Links (comma-separated)"
          name="images"
          value={article.images.join(",")}
          onChange={handleChange}
        />

        <button type="submit">Update Article</button>
      </form>
    </div>
  );
}

export default ArticleEdit;
