import React, { useState, useEffect } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import { getArticle, deleteArticle } from "../../services/atricles.js";
import authService from "../../services/auth.js";
import Likes from "../../components/LikesComp.jsx";
import * as commentService from "../../services/comments.js";
import CreateComment from "../../components/CreateComment.jsx";
// import CommentComp from "../../components/CommentComp.jsx";

function ArticeInfo() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [article, setArticle] = useState(null);
  const [showEdit, setShowEdit] = useState(false);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userData = await authService.getUserData();
        setUser(userData);
      } catch (error) {
        console.log(error);
      }
    };
    fetchUser();
  }, []);

  useEffect(() => {
    const fetchArticle = async () => {
      try {
        const articleData = await getArticle(id);
        setArticle(articleData);
      } catch (error) {
        console.error("Error fetching article:", error);
      }
    };

    fetchArticle();
  }, [id]);

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const commentsData = await commentService.getCommentsForArticle(id);
        setComments(commentsData);
      } catch (error) {
        console.error("Error fetching comments:", error);
      }
    };

    fetchComments();
  }, [id]);

  const fetchNewComments = async () => {
    try {
      const newComment = await commentService.getCommentsForArticle(id);
      setComments(newComment);
    } catch (error) {
      console.error("Error fetching updated comments:", error);
    }
  };

  const handleDeleteComment = async (commentId) => {
    try {
      await commentService.deleteComment(commentId);
      setComments((prevComments) =>
        prevComments.filter((comment) => comment._id !== commentId)
      );
    } catch (error) {
      console.error("Error deleting comment:", error);
    }
  };

  useEffect(() => {
    if (user && article && user._id === article.author) {
      setShowEdit(true);
    }
  }, [user, article]);

  if (!article) {
    return <div>Loading...</div>;
  }

  return (
    <div className="pt-8 pb-16 lg:pt-16 lg:pb-24 bg-white dark:bg-gray-900 text-gray-500">
      {/* <p>{article._id}</p> */}
      <div className="mx-auto w-full max-w-2xl format format-sm sm:format-base lg:format-lg format-blue dark:format-invert">
        <div className="mb-4 lg:mb-6 not-format">
          <div className="flex items-center mb-6 not-italic">
          <p>{article.author}</p>
            <p>date written</p>
            </div>
          <h2 className="mb-4 text-3xl font-extrabold leading-tight text-gray-900 lg:mb-6 lg:text-4xl dark:text-white">
            {article.title}
          </h2>
        </div>

        <p className="lead">{article.content}</p>
        <img src={article.images} alt="images" />

        {/* Temporary testing Likes component */}
        {/* {console.log(user._id)} */}
      </div>
      {user && <Likes articleId={id} user={user} />}

      <div className="border-2 border-black">
        <h2>Comments</h2>
        {comments.map((comment) => (
          <div key={comment._id}>
            <p>{comment.content}</p>
            {comment.creator === user?._id && (
              <button onClick={() => handleDeleteComment(comment._id)}>
                Delete
              </button>
            )}
          </div>
        ))}

        {user && <CreateComment id={id} fetchNewComments={fetchNewComments} />}
      </div>

      {/* Temporary placement of edit link for testing */}
      {showEdit && <Link to={`/article/${id}/edit`}>Edit Article</Link>}


    </div>
  );
}

export default ArticeInfo;
